mapper.select:
SELECT seat_status_id
FROM SEAT_STATUS
WHERE schedule_id = #{scheduleid} AND seat_id = #{seatId}

mapper.update:
UPDATE Seat_Status
SET status_id = 3
WHERE seat_status_id = #{seatStatusId}

UPDATE Seat_Status
SET status_id = 1
WHERE seat_status_id = #{seatStatusId}

UPDATE Reservation
SET reservation_status_id = 3
WHERE reservation_id  = #{reservationId}
